const getBear = (hue, variant) => {
    if (variant) {
        return VARIANTS[variant](hue)
    } else {
        const variantKey =
            Object.keys(VARIANTS)[
                Math.floor(Math.random() * Object.keys(VARIANTS).length)
            ]
        return VARIANTS[variantKey](hue)
    }
}
